        구군 :
        <select id="gugun" v-model="gugun" >
          <option :value="item.gugunCode" v-for="(item, index) in guguns" :key="index">
            {{ item.gugunName }}
          </option>
        </select>
        읍면동 :
        <select id="dong" v-model="dong">
          <option :value="item.dong" v-for="(item, index) in dongs" :key="index">
            {{ item.dong }}
          </option>
        </select>
      </div>
      <hr />

      <div class="row" style="margin-left: 20px">
        <div>
          학군
          <div class="custom-control custom-radio custom-control-inline" style="margin-left: 30px">
            <input
              v-model="checkedSchool"
              type="checkbox"
              class="custom-control-input"
              id="초등학교"
              name="초등학교"
              value="초등학교"
            />
            <label class="custom-control-label" for="초등학교">초등학교</label>
          </div>
          <div class="custom-control custom-radio custom-control-inline">
            <input
              v-model="checkedSchool"
              type="checkbox"
              class="custom-control-input"
              id="중학교"
              name="중학교"
              value="중학교"
            />
            <label class="custom-control-label" for="중학교">중학교</label>
          </div>
          <div class="custom-control custom-radio custom-control-inline">
            <input
              v-model="checkedSchool"
              type="checkbox"
              class="custom-control-input"
              id="고등학교"
              name="고등학교"
              value="고등학교"
            />
            <label class="custom-control-label" for="고등학교">고등학교</label>
            <br />
          </div>
        </div>
      </div>
      <hr />

      <br /><br />
    </div>

    <v-container>
      <v-card-title>
        <h1>매물 검색 결과</h1>
        <v-spacer></v-spacer>
        <v-text-field
          v-model="search"
          append-icon="mdi-magnify"
          label="Search"
          single-line
          hide-details
        ></v-text-field>
      </v-card-title>
      <v-data-table
        :headers="headers"
        :items="apts"
        :items-per-page="5"
        :search="search"
        :footer-props="{
          showFirstLastPage: true,
          firstIcon: 'mdi-arrow-collapse-left',
          lastIcon: 'mdi-arrow-collapse-right',
          prevIcon: 'mdi-minus',
          nextIcon: 'mdi-plus',
        }"
