        <template v-slot:body="{ items }">
          <tbody>
            <tr v-for="(item, index) in items" :key="index">
              <td>{{ item.no }}</td>
              <td>{{ item.dong }}</td>
              <td>{{ item.aptName }}</td>
              <td>{{ item.jibun }}</td>
              <td>{{ item.code }}</td>
              <td>{{ item.lat }}</td>
              <td>{{ item.lng }}</td>
            </tr>
          </tbody>
        </template>
      </v-data-table>
    </v-container>

    <br />

    <div id="map">
      <hr />
      <div id="map"></div>
    </div>
  </div>
</template>

<script>
import axios from 'axios';

// const colorArr = ['table-primary', 'table-success', 'table-danger'];

export default {
  data() {
    return {
      search: '',
      headers: [
        { text: '번호', value: 'title' },
        { text: '법정동', value: 'title' },
        { text: '아파트', value: 'title' },
        { text: '지번', value: 'title' },
        { text: '지역코드', value: 'title' },
        { text: '위도', value: 'title' },
        { text: '경도', value: 'title' },
      ],
      school: [],
      keyword: '',
      sidos: [],
      sido: {},
      guguns: [],
      gugun: {},
      dongs: [],
      dong: {},
      apts: [],
      checkedSchool: [],
      schools: [], //좌표, 학교이름 리턴하는 객체배열 watch
      checkedStation: [],
      stations: [],
      positions: [],
      schoolpositions: [],
      customOverlay: '',
      check: Boolean,
    };
  },

  mounted() {
    //  if (window.kakao && window.kakao.maps) {
    //   this.initMap(); // 초기 맵 으로 이동 (지금은 카카오 좌표)
    // } else {

    const script = document.createElement('script');
    /* global kakao */
    script.onload = () => kakao.maps.load(this.initMap);
    script.src =
      'http://dapi.kakao.com/v2/maps/sdk.js?autoload=false&appkey=ee37a1035dfa14e56abd773fe878b0fc';
    document.head.appendChild(script);

    var container = document.getElementById('map');
    var options = {
      center: new kakao.maps.LatLng(36.3543926, 127.2971463),
      level: 3,
    }; // 지도를 표시할 di
    var map = new kakao.maps.Map(container, options);

    var positions = [
      {
        title: '대전싸피',
        latlng: new kakao.maps.LatLng(36.3543926, 127.2971463),
      },
    ];
    var imageSrc = 'https://www.flaticon.com/svg/static/icons/svg/609/609803.svg';

    for (var i = 0; i < positions.length; i++) {
      // 마커 이미지의 이미지 크기 입니다.
      var imageSize = new kakao.maps.Size(30, 40);

      // 마커 이미지를 생성합니다
      var markerImage = new kakao.maps.MarkerImage(imageSrc, imageSize);

      // 마커를 생성합니다
      var marker = new kakao.maps.Marker({
        map: map, // 마커를 표시할 지도
        position: positions[i].latlng, // 마커를 표시할 위치
        title: positions[i].title, // 마커의 타이틀, 마커에 마우스를 올리면 타이틀이 표시됩니다
        image: markerImage, // 마커 이미지
      });
      marker.setMap(map);
    }
    map.setMapTypeId(kakao.maps.MapTypeId.maps);
    // }
  },

  methods: {
    // initMap() {
    //   var container = document.getElementById('map');
    //   var options = {
    //     center: new kakao.maps.LatLng(33.450879, 126.56994),
    //     level: 3,
    //   };

    //   var markerPosition = new kakao.maps.LatLng(33.450879, 126.56994);

    //   var marker = new kakao.maps.Marker({
    //     position: markerPosition,
    //   });
    //   var map = new kakao.maps.Map(container, options);
    //   marker.setMap(map);
    //   map.setMapTypeId(kakao.maps.MapTypeId.kakao);
    // },
    searchApt() {
      axios
        .get(`http://localhost:7777/happyhouse/map/apt/search/${this.keyword}`)
        .then(({ data }) => {
          this.apts = data;
        })
        .catch((err) => {
          console.log(err);
        });
    },
  },

  created() {
    // 처음에 시도정보 가져오기
    axios
      .get(`http://localhost:7777/happyhouse/map/sido`)
      .then(({ data }) => {
        this.sidos = data;
      })
      .catch((err) => {
        console.log(err);
      });
  },
  watch: {
    keyword() {
      //여기에 지도 실시간 변경
      axios
        .get(`http://localhost:7777/happyhouse/map/apt/search/${this.keyword}`)
        .then(({ data }) => {
          //data  ->  apt 정보가 담긴 객체 받아왔어
          // console.log(data);
          this.apts = data; //apt에 전달해서 출력에 활용하고
        }) //then
        .catch((err) => {
          console.log(err);
        });
    },
    checkedSchool() {
      console.log(this.checkedSchool);

      const body = {
        checkedSchool: this.checkedSchool,
        dong: this.dong,
      };
      console.log(body);
      axios
        .post(`http://localhost:7777/happyhouse/map/school`, body)
        .then(({ data }) => {
          console.log(data);
          this.schools = data;
        })
        .catch((err) => {
          console.log(err);
        });
    },
    checkedStation() {
      //checkedStation 변경되면
      console.log('checkedStation()들어옴');
      console.log(this.checkedStation[0]);
      if (this.checkedStation[0] != null) {
        this.check = true;
      } else {
        this.check = false;
      }
      const body2 = {
        check: this.check,
        dong: this.dong,
      };
      console.log(body2);
      axios
        .post(`http://localhost:7777/happyhouse/map/station`, body2) //치하철이 체크 되고 안되고를 보냈음
        .then(({ data }) => {
          this.stations = data; //stations 에 지하철 좌표, 역명 받아옴
        })
        .catch((err) => {
          console.log(err);
        });
    },

    sido() {
      console.log(this.sido);
      axios
        .get(`http://localhost:7777/happyhouse/map/gugun/${this.sido}`)
        .then(({ data }) => {
          this.guguns = data;
        })
        .catch((err) => {
          console.log(err);
        });
    },
    gugun() {
      console.log(this.gugun);
      axios
        .get(`http://localhost:7777/happyhouse/map/dong/${this.gugun}`)
        .then(({ data }) => {
          this.dongs = data;
        })
        .catch((err) => {
          console.log(err);
        });
    },
    dong() {
      //여기에 지도 실시간 변경
      console.log(this.dong);
      axios
        .get(`http://localhost:7777/happyhouse/map/apt/${this.dong}`)
        .then(({ data }) => {
          this.apts = data;

          // geocode(data);
        })
        .catch((err) => {
          console.log(err);
        });
      this.checkedSchool = [];
      this.checkedStation = [];
    },
    // stations(){

    // }
    schools() {
      //스쿨 찍을때 지하철(체크 되어있으면) , 집 모두 같이 표현되어야함
      console.log('schools()들어옴');
      console.log(this.schools);
      // 초중고가 변경 될 때 마다
      const script = document.createElement('script');

      script.onload = () => kakao.maps.load(this.initMap);
      script.src =
        'http://dapi.kakao.com/v2/maps/sdk.js?autoload=false&appkey=ee37a1035dfa14e56abd773fe878b0fc';
      document.head.appendChild(script);

      var container = document.getElementById('map');
      var options = {
        center: new kakao.maps.LatLng(33.450705, 126.570677),
        level: 3,
      }; // 지도를 표시할 div
      var map = new kakao.maps.Map(container, options);

      this.schoolpositions = []; //이전 남아있는 마커 없애기 위해
      this.schools.forEach((element) => {
        // 초중고가 변할 때마다 school 값 포지션에 넣어주고 마커 생성

        const schoolposition = {
          schoolname: element.school_name, //학교 이름
          latlng: new kakao.maps.LatLng(element.school_lat, element.school_lng), // 경도위도
          jibun: element.school_jibun, //주소
        };

        this.schoolpositions = [...this.schoolpositions, schoolposition]; //schoolpositions[{schoolposition객체},{schoolposition객체},{schoolposition객체}]
      }); //foreach
      var imageSrc = 'https://www.flaticon.com/svg/static/icons/svg/609/609803.svg';
      var schoolimage = 'https://www.flaticon.com/premium-icon/icons/svg/3252/3252806.svg';
      var bounds = new kakao.maps.LatLngBounds();

      for (let i = 0; i < this.positions.length; i++) {
        // 배열의 좌표들이 잘 보이게 마커를 지도에 추가합니다
        // marker = new kakao.maps.Marker({ position: this.positions[i].latlng });
        // marker.setMap(map);

        // LatLngBounds 객체에 좌표를 추가합니다
        bounds.extend(this.positions[i].latlng);
      }

      for (var i = 0; i < this.schoolpositions.length; i++) {
        // 마커 이미지의 이미지 크기 입니ek.
        let imageSize = new kakao.maps.Size(30, 40);

        // 마커 이미지를 생성합니다
        let markerImage = new kakao.maps.MarkerImage(schoolimage, imageSize);

        // const customOverlay = new kakao.maps.CustomOverlay({
        //   position: this.positions[i].latlng,
        //   content: this.positions[i].content,
        //   xAnchor: 0.3,
        //   yAnchor: 0.91,
        // });
        // 마커를 생성합니다
        const marker1 = new kakao.maps.Marker({
          map: map, // 마커를 표시할 지도
          position: this.schoolpositions[i].latlng, // 마커를 표시할 위치
          title: this.schoolpositions[i].schoolname, // 마커의 타이틀, 마커에 마우스를 올리면 타이틀이 표시됩니다
          image: markerImage, // 마커 이미지
        });

        // 마커에 클릭이벤트를 등록합니다
        // kakao.maps.event.addListener(marker, 'click', function() {
        //   customOverlay.setMap(map);
        // });

        console.log('초중고 변경 시 집의 학교마커: ' + marker1);
        marker1.setMap(map);
      }
      for (var i = 0; i < this.positions.length; i++) {
        // 마커 이미지의 이미지 크기 입니ek.
        let imageSize = new kakao.maps.Size(30, 40);

        // 마커 이미지를 생성합니다
        let markerImage = new kakao.maps.MarkerImage(imageSrc, imageSize);

        const customOverlay = new kakao.maps.CustomOverlay({
          position: this.positions[i].latlng,
          content: this.positions[i].content,
          xAnchor: 0.3,
          yAnchor: 0.91,
        });
        // 마커를 생성합니다
        const marker2 = new kakao.maps.Marker({
          map: map, // 마커를 표시할 지도
          position: this.positions[i].latlng, // 마커를 표시할 위치
          title: this.positions[i].title, // 마커의 타이틀, 마커에 마우스를 올리면 타이틀이 표시됩니다
          image: markerImage, // 마커 이미지
        });

        // 마커에 클릭이벤트를 등록합니다
        kakao.maps.event.addListener(marker2, 'click', function() {
          customOverlay.setMap(map);
        });

        console.log('초중고 변경 시 집의 마커: ' + marker2);
        marker2.setMap(map);
      }

      map.setBounds(bounds); //맵 집의 중앙으로
      map.setMapTypeId(kakao.maps.MapTypeId.maps); //맵보이게
    },

    apts() {
      //keyword나 읍면동 변경될 때마다
      const script = document.createElement('script');

      script.onload = () => kakao.maps.load(this.initMap);
      script.src =
        'http://dapi.kakao.com/v2/maps/sdk.js?autoload=false&appkey=ee37a1035dfa14e56abd773fe878b0fc';
      document.head.appendChild(script);

      var container = document.getElementById('map');
      var options = {
        center: new kakao.maps.LatLng(33.450705, 126.570677),
        level: 3,
      }; // 지도를 표시할 div
      var map = new kakao.maps.Map(container, options);

      this.positions = [];
      this.apts.forEach((element) => {
        // apt가 변할 때마다 apt 값 포지션에 넣어주고 마커 생성

        const position = {
          title: element.aptName, //아파트 이름
          latlng: new kakao.maps.LatLng(element.lat, element.lng), // 경도위도
          dealAmount: element.dealAmount, //매매가
          area: element.area, //면적
          //나중에 룸메 구하는 사람 아이디, 충원 인원 추가?

          ///////////////////////////각 마커의 오버레이 콘텐츠 /////////////////////
          content:
            '<div class="overlaybox">' +
            // '    <button class="btn btn-danger" :click="customOverlay.setMap(null);">닫기</button>' +
            '    <div class="boxtitle">' +
            element.aptName +
            '</div>' +
            '    <div class="first">' +
            '        <div class="movietitle text">내부 사진</div>' +
            '    </div>' +
            '    <ul>' +
            '        <li class="up">' +
            '            <span class="number">매매가: </span>' +
            '            <span class="title">' +
            element.dealAmount +
            '</span>' +
            // '            <span class="count">집 값 상승</span>' +
            '            <span class="arrow up"></span>' +
            '        </li>' +
            '        <li>' +
            '            <span class="number">평 수: </span>' +
            '            <span class="title">' +
            element.area +
            '</span>' +
            '            <span class="count">제곱미터</span>' +
            '        </li>' +
            '        <li>' +
            '            <span class="number">건축년도: </span>' +
            '            <span class="title">' +
            element.buildYear +
            '</span>' +
            '        </li>' +
            '        <li>' +
            '            <span class="number">거래년도: </span>' +
            '            <span class="title">' +
            element.dealYear +
            '</span>' +
            '        </li>' +
            // "        <li>" +
            // '            <a href="" class="number">룸메이트 찾기</a>' +
            // '            <span class="count"></span>' +
            // "        </li>" +
            '    </ul>' +
            '</div>',
        };
        this.positions = [...this.positions, position]; //positions[{position객체},{position객체},{position객체}]
      }); //////////// foreach

      var bounds = new kakao.maps.LatLngBounds();

      for (let i = 0; i < this.positions.length; i++) {
        // LatLngBounds 객체에 좌표를 추가합니다
        bounds.extend(this.positions[i].latlng);
      }

      ////////////////////////맵 설정//////////////////////////

      var imageSrc = 'https://www.flaticon.com/svg/static/icons/svg/609/609803.svg';

      for (var i = 0; i < this.positions.length; i++) {
        // 마커 이미지의 이미지 크기 입니ek.
        var imageSize = new kakao.maps.Size(30, 40);

        // 마커 이미지를 생성합니다
        var markerImage = new kakao.maps.MarkerImage(imageSrc, imageSize);

        const customOverlay = new kakao.maps.CustomOverlay({
          position: this.positions[i].latlng,
          content: this.positions[i].content,
          xAnchor: 0.3,
          yAnchor: 0.91,
        });
        // 마커를 생성합니다
        const marker = new kakao.maps.Marker({
          map: map, // 마커를 표시할 지도
          position: this.positions[i].latlng, // 마커를 표시할 위치
          title: this.positions[i].title, // 마커의 타이틀, 마커에 마우스를 올리면 타이틀이 표시됩니다
          image: markerImage, // 마커 이미지
        });

        // 마커에 클릭이벤트를 등록합니다
        kakao.maps.event.addListener(marker, 'click', function() {
          customOverlay.setMap(map);
        });

        console.log(marker);
        marker.setMap(map);
      }
      console.log('apt()들어옴');
      map.setBounds(bounds);

      map.setMapTypeId(kakao.maps.MapTypeId.maps);
    },
  }, //watch
};
</script>

